    8((8)) --> net["fa:fa-share-alt Network ."];
    9((9)) --> report["fa:fa-file-text-o Report "];
    vis["fa:fa-braille Plot."];
    data --> 2;
    preproc --> 3;
    preproc --> 5;
    preproc --> 6; 
    preproc --> 7;
